eadAsStringAsync(); var odataResponse = JsonConvert.DeserializeObject<ODataResponse<AlfaContractEligibility>>(responseData); var contractEligibilities = odataResponse.Value; var groupedContractEligibilities = contractEligibilities .Where(e => e.AlfaType != 0 && e.AlfaType != 3) // Exclude entries where alfa_type is 0 .GroupBy(e => e.AlfaType) .Select(g => new { AlfaType = g.Key, Eligibilities = g.Select(e => new { e.AlfaName, e.AlfaMowahadCountryId, e.AlfaAddressTypeId }) }) .ToList(); return Json(groupedContractEligibilities); } } private string GetNameForAddressType(string addressTypeValue) { Dictionary<string, string> addressTypeNames = new Dictionary<string, string>() { { \\"3dbf1e46-0107-ee11-8985-005056b358ac\\", \\"National Address ID\\" }, { \\"5c872261-0107-ee11-8985-005056b358ac\\", \\"Descriptive Address\\" }, {\\"a3c80f96-0107-ee11-8985-005056b358ac\\",\\"International Address\\" }, { \\"3a79b77e-0107-ee11-8985-005056b358ac\\", \\"Office\\" }, {\\"1c1c3a8b-0107-ee11-8985-005056b358ac\\",\\"Parcel Station\\" }, {\\"1bf9e16f-0107-ee11-8985-005056b358ac\\",\\"P.O Box\\" }, }; if (addressTypeNames.TryGetValue(addressTypeValue, out string name)) { return name; } else { return \\"Unknown\\"; } } public class ODataResponse<T> { [JsonProperty(\\"@odata.context\\")] public string Context { get; set; } public List<T> Value { get; set; } } public class AlfaContractEligibility { [JsonProperty(\\"alfa_type\\")] public int AlfaType { get; set; } [JsonProperty(\\"alfa_name\\")] public string AlfaName { get; set; } [JsonProperty(\\"alfa_mowahadcityid\\")] public string AlfaMowahadCityId { get; set; } [JsonProperty(\\"alfa_mowahadcountryid\\")] public string AlfaMowahadCountryId { get; set; } [JsonProperty(\\"alfa_addresstypeid\\")] public string AlfaAddressTypeId { get; set; } [JsonProperty(\\"_alfa_addresstype_value\\")] public string AlfaAddressTypeValue { get; set; } } }","output":"The provided code is a C# code snippet that includes an ApiController class called \\"